import React, { useEffect, useState } from "react";
import QRCode from "qrcode.react";

function App() {
  const [message, setMessage] = useState("Memuat...");
  const [qr, setQr] = useState(null);

  useEffect(() => {
    fetch("http://localhost:5000/")
      .then((res) => res.json())
      .then((data) => setMessage(data.message))
      .catch(() => setMessage("Gagal mengambil data dari backend"));

    // Fetch QR code setiap 2 detik (polling)
    const interval = setInterval(() => {
      fetch("http://localhost:5000/get-qr")
        .then((res) => res.json())
        .then((data) => setQr(data.qr))
        .catch(() => setQr(null));
    }, 2000);

    return () => clearInterval(interval);
  }, []);

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100">
      <div className="p-8 bg-white rounded shadow text-center">
        <h1 className="text-2xl font-bold mb-4">WhatsApp Gateway</h1>
        <p className="text-lg mb-4">{message}</p>
        {qr && (
          <div>
            <p className="mb-2 font-semibold">Scan QR Code WhatsApp:</p>
            <QRCode value={qr} size={256} />
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
