import React, { useEffect, useState } from "react";
import { QRCodeCanvas } from "qrcode.react";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";

function Dashboard() {
  return (
    <div>
      <h2 className="text-xl font-bold mb-4">Dashboard</h2>
      <p>Selamat datang di WhatsApp Gateway!</p>
    </div>
  );
}

function KirimPesan({ connected, qr, handleSend, to, setTo, text, setText, sendStatus }) {
  return (
    <div>
      <h2 className="text-xl font-bold mb-4">Kirim Pesan WhatsApp</h2>
      {!connected && qr && (
        <div className="mb-8 flex flex-col items-center">
          <p className="mb-2 font-semibold">Scan QR Code WhatsApp:</p>
          <QRCodeCanvas value={qr} size={256} />
        </div>
      )}
      {connected && (
        <form onSubmit={handleSend} className="space-y-4">
          <div>
            <input
              type="text"
              placeholder="Nomor WhatsApp (628xxxxxx)"
              value={to}
              onChange={(e) => setTo(e.target.value)}
              className="border p-2 rounded w-full"
              required
            />
          </div>
          <div>
            <textarea
              placeholder="Isi pesan"
              value={text}
              onChange={(e) => setText(e.target.value)}
              className="border p-2 rounded w-full"
              required
            />
          </div>
          <button
            type="submit"
            className="bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600"
          >
            Kirim Pesan
          </button>
          {sendStatus && <div className="mt-2 text-sm">{sendStatus}</div>}
        </form>
      )}
    </div>
  );
}

function PesanTerkirim() {
  const [data, setData] = useState([]);
  useEffect(() => {
    fetch("http://localhost:5000/sent-messages")
      .then((res) => res.json())
      .then(setData)
      .catch(() => setData([]));
  }, []);
  return (
    <div>
      <h2 className="text-xl font-bold mb-4">Pesan Terkirim</h2>
      <div className="overflow-x-auto">
        <table className="min-w-full border text-sm">
          <thead>
            <tr className="bg-gray-100">
              <th className="border px-2 py-1">No</th>
              <th className="border px-2 py-1">Nomor Tujuan</th>
              <th className="border px-2 py-1">Pesan</th>
              <th className="border px-2 py-1">Waktu</th>
            </tr>
          </thead>
          <tbody>
            {data.length === 0 && (
              <tr><td colSpan={4} className="text-center py-2">Belum ada data</td></tr>
            )}
            {data.map((row, i) => (
              <tr key={row.id}>
                <td className="border px-2 py-1">{i + 1}</td>
                <td className="border px-2 py-1">{row.to}</td>
                <td className="border px-2 py-1">{row.message}</td>
                <td className="border px-2 py-1">{new Date(row.timestamp).toLocaleString()}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

function PesanMasuk() {
  const [data, setData] = useState([]);
  useEffect(() => {
    fetch("http://localhost:5000/received-messages")
      .then((res) => res.json())
      .then(setData)
      .catch(() => setData([]));
  }, []);
  return (
    <div>
      <h2 className="text-xl font-bold mb-4">Pesan Masuk</h2>
      <div className="overflow-x-auto">
        <table className="min-w-full border text-sm">
          <thead>
            <tr className="bg-gray-100">
              <th className="border px-2 py-1">No</th>
              <th className="border px-2 py-1">Nomor Pengirim</th>
              <th className="border px-2 py-1">Pesan</th>
              <th className="border px-2 py-1">Waktu</th>
            </tr>
          </thead>
          <tbody>
            {data.length === 0 && (
              <tr><td colSpan={4} className="text-center py-2">Belum ada data</td></tr>
            )}
            {data.map((row, i) => (
              <tr key={row.id}>
                <td className="border px-2 py-1">{i + 1}</td>
                <td className="border px-2 py-1">{row.from}</td>
                <td className="border px-2 py-1">{row.message}</td>
                <td className="border px-2 py-1">{new Date(row.timestamp).toLocaleString()}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

function Chatbot() {
  const [active, setActive] = useState(false);
  const [loading, setLoading] = useState(false);
  useEffect(() => {
    // Tidak ada endpoint status, jadi asumsikan off saat reload
    setActive(false);
  }, []);
  const toggle = async () => {
    setLoading(true);
    const res = await fetch("http://localhost:5000/chatbot-toggle", { method: "POST" });
    const data = await res.json();
    setActive(data.active);
    setLoading(false);
  };
  return (
    <div>
      <h2 className="text-xl font-bold mb-4">Fitur Chatbot</h2>
      <p className="mb-4">Chatbot akan membalas otomatis pesan "hai" dengan "Halo, ada yang bisa kami bantu?"</p>
      <button
        onClick={toggle}
        disabled={loading}
        className={`px-4 py-2 rounded text-white ${active ? 'bg-green-600' : 'bg-gray-400'} hover:bg-green-700`}
      >
        {loading ? 'Memproses...' : active ? 'Matikan Chatbot' : 'Aktifkan Chatbot'}
      </button>
      <div className="mt-4 text-sm">Status: <span className={active ? 'text-green-600' : 'text-gray-600'}>{active ? 'Aktif' : 'Nonaktif'}</span></div>
    </div>
  );
}

function AutoReply() {
  const [data, setData] = useState([]);
  const [keyword, setKeyword] = useState("");
  const [reply, setReply] = useState("");
  const [editId, setEditId] = useState(null);
  const [error, setError] = useState("");

  const fetchData = () => {
    fetch("http://localhost:5000/auto-replies")
      .then(res => res.json())
      .then(setData)
      .catch(() => setData([]));
  };
  useEffect(fetchData, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    const url = editId ? `http://localhost:5000/auto-replies/${editId}` : "http://localhost:5000/auto-replies";
    const method = editId ? "PUT" : "POST";
    const res = await fetch(url, {
      method,
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ keyword, reply })
    });
    const result = await res.json();
    if (result.status === "success") {
      setKeyword(""); setReply(""); setEditId(null); fetchData();
    } else {
      setError(result.message || "Gagal menyimpan");
      console.error('AutoReply error:', result);
    }
  };

  const handleEdit = (row) => {
    setEditId(row.id); setKeyword(row.keyword); setReply(row.reply);
  };
  const handleDelete = async (id) => {
    if (!window.confirm("Hapus auto reply ini?")) return;
    await fetch(`http://localhost:5000/auto-replies/${id}`, { method: "DELETE" });
    fetchData();
  };
  const handleCancel = () => {
    setEditId(null); setKeyword(""); setReply(""); setError("");
  };

  return (
    <div>
      <h2 className="text-xl font-bold mb-4">Auto Reply</h2>
      <form onSubmit={handleSubmit} className="mb-4 space-y-2">
        <div>
          <input type="text" className="border p-2 rounded w-64 mr-2" placeholder="Keyword" value={keyword} onChange={e => setKeyword(e.target.value)} required />
          <input type="text" className="border p-2 rounded w-64 mr-2" placeholder="Balasan" value={reply} onChange={e => setReply(e.target.value)} required />
          <button type="submit" className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600">{editId ? "Update" : "Tambah"}</button>
          {editId && <button type="button" onClick={handleCancel} className="ml-2 px-4 py-2 rounded bg-gray-400 text-white">Batal</button>}
        </div>
        {error && <div className="text-red-500 text-sm">{error}</div>}
      </form>
      <div className="overflow-x-auto">
        <table className="min-w-full border text-sm">
          <thead>
            <tr className="bg-gray-100">
              <th className="border px-2 py-1">No</th>
              <th className="border px-2 py-1">Keyword</th>
              <th className="border px-2 py-1">Balasan</th>
              <th className="border px-2 py-1">Aksi</th>
            </tr>
          </thead>
          <tbody>
            {data.length === 0 && <tr><td colSpan={4} className="text-center py-2">Belum ada data</td></tr>}
            {data.map((row, i) => (
              <tr key={row.id}>
                <td className="border px-2 py-1">{i + 1}</td>
                <td className="border px-2 py-1">{row.keyword}</td>
                <td className="border px-2 py-1">{row.reply}</td>
                <td className="border px-2 py-1">
                  <button onClick={() => handleEdit(row)} className="px-2 py-1 bg-yellow-400 rounded mr-2">Edit</button>
                  <button onClick={() => handleDelete(row.id)} className="px-2 py-1 bg-red-500 text-white rounded">Hapus</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

function App() {
  const [message, setMessage] = useState("Memuat...");
  const [qr, setQr] = useState(null);
  const [to, setTo] = useState("");
  const [text, setText] = useState("");
  const [sendStatus, setSendStatus] = useState("");
  const [connected, setConnected] = useState(false);
  const [loadingLogout, setLoadingLogout] = useState(false);

  // Polling status koneksi dan QR code
  useEffect(() => {
    const statusInterval = setInterval(() => {
      fetch("http://localhost:5000/status")
        .then((res) => res.json())
        .then((data) => setConnected(data.connected))
        .catch(() => setConnected(false));
    }, 2000);

    let qrInterval = null;
    if (!connected) {
      qrInterval = setInterval(() => {
        fetch("http://localhost:5000/get-qr")
          .then((res) => res.json())
          .then((data) => setQr(data.qr))
          .catch(() => setQr(null));
      }, 2000);
    } else {
      setQr(null);
    }

    return () => {
      clearInterval(statusInterval);
      if (qrInterval) clearInterval(qrInterval);
    };
  }, [connected]);

  useEffect(() => {
    fetch("http://localhost:5000/")
      .then((res) => res.json())
      .then((data) => setMessage(data.message))
      .catch(() => setMessage("Gagal mengambil data dari backend"));
  }, []);

  const handleSend = async (e) => {
    e.preventDefault();
    setSendStatus("Mengirim...");
    try {
      const res = await fetch("http://localhost:5000/send-message", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          to: to.includes("@s.whatsapp.net") ? to : to + "@s.whatsapp.net",
          message: text,
        }),
      });
      const data = await res.json();
      if (data.status === "success") {
        setSendStatus("Pesan terkirim!");
      } else {
        setSendStatus("Gagal: " + data.message);
      }
    } catch (err) {
      setSendStatus("Gagal mengirim pesan");
    }
  };

  const handleLogout = async () => {
    setLoadingLogout(true);
    try {
      await fetch("http://localhost:5000/logout", { method: "POST" });
      setConnected(false); // Ini sudah benar
      setQr(null);         // Tambahkan ini untuk reset QR code
      setSendStatus("");
      setTo("");
      setText("");
    } catch (err) {}
    setLoadingLogout(false);
  };

  return (
    <Router>
      <div className="min-h-screen flex bg-gray-100">
        {/* Sidebar */}
        <aside className="w-64 bg-green-700 text-white flex flex-col py-6 px-4">
          <div className="text-2xl font-bold mb-8 text-center">WA Gateway</div>
          <nav className="flex-1 space-y-4">
            <Link to="/" className="block py-2 px-3 rounded hover:bg-green-800">Dashboard</Link>
            <Link to="/kirim" className="block py-2 px-3 rounded hover:bg-green-800">Kirim Pesan</Link>
            <Link to="/sent" className="block py-2 px-3 rounded hover:bg-green-800">Pesan Terkirim</Link>
            <Link to="/inbox" className="block py-2 px-3 rounded hover:bg-green-800">Pesan Masuk</Link>
             /
            <Link to="/autoreply" className="block py-2 px-3 rounded hover:bg-green-800">Auto Reply</Link>
          </nav>
          <div className="mt-8">
            <button
              onClick={handleLogout}
              disabled={loadingLogout}
              className="w-full bg-red-500 hover:bg-red-600 text-white py-2 rounded disabled:opacity-50"
            >
              {loadingLogout ? "Menghapus Sesi..." : "Logout / Hapus Sesi"}
            </button>
          </div>
        </aside>
        {/* Main Content */}
        <div className="flex-1 flex flex-col">
          {/* Navbar */}
          <header className="bg-white shadow px-8 py-4 flex items-center justify-between">
            <div className="text-xl font-semibold">WhatsApp Gateway</div>
            <div className="text-sm text-gray-600">{connected ? "Status: Terhubung" : "Status: Belum Terhubung"}</div>
          </header>
          {/* Content */}
          <main className="flex-1 p-8 bg-gray-100 overflow-y-auto">
            <div className="w-full max-w-6xl mx-auto bg-white rounded shadow p-4">
              <Routes>
                <Route path="/" element={<Dashboard />} />
                <Route path="/kirim" element={
                  <KirimPesan
                    connected={connected}
                    qr={qr}
                    handleSend={handleSend}
                    to={to}
                    setTo={setTo}
                    text={text}
                    setText={setText}
                    sendStatus={sendStatus}
                  />
                } />
                <Route path="/sent" element={<PesanTerkirim />} />
                <Route path="/inbox" element={<PesanMasuk />} />
                <Route path="/chatbot" element={<Chatbot />} />
                <Route path="/autoreply" element={<AutoReply />} />
              </Routes>
            </div>
          </main>
        </div>
      </div>
    </Router>
  );
}

export default App;
