import React, { useEffect, useState } from "react";
import { QRCodeCanvas } from "qrcode.react";

function App() {
  const [message, setMessage] = useState("Memuat...");
  const [qr, setQr] = useState(null);
  const [to, setTo] = useState("");
  const [text, setText] = useState("");
  const [sendStatus, setSendStatus] = useState("");
  const [connected, setConnected] = useState(false);
  const [loadingLogout, setLoadingLogout] = useState(false);

  // Polling status koneksi dan QR code
  useEffect(() => {
    // Cek status koneksi setiap 2 detik
    const statusInterval = setInterval(() => {
      fetch("http://localhost:5000/status")
        .then((res) => res.json())
        .then((data) => setConnected(data.connected))
        .catch(() => setConnected(false));
    }, 2000);

    // Fetch QR code setiap 2 detik (hanya jika belum konek)
    let qrInterval = null;
    if (!connected) {
      qrInterval = setInterval(() => {
        fetch("http://localhost:5000/get-qr")
          .then((res) => res.json())
          .then((data) => setQr(data.qr))
          .catch(() => setQr(null));
      }, 2000);
    } else {
      setQr(null);
    }

    return () => {
      clearInterval(statusInterval);
      if (qrInterval) clearInterval(qrInterval);
    };
  }, [connected]);

  useEffect(() => {
    fetch("http://localhost:5000/")
      .then((res) => res.json())
      .then((data) => setMessage(data.message))
      .catch(() => setMessage("Gagal mengambil data dari backend"));
  }, []);

  const handleSend = async (e) => {
    e.preventDefault();
    setSendStatus("Mengirim...");
    try {
      const res = await fetch("http://localhost:5000/send-message", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          to: to.includes("@s.whatsapp.net") ? to : to + "@s.whatsapp.net",
          message: text,
        }),
      });
      const data = await res.json();
      if (data.status === "success") {
        setSendStatus("Pesan terkirim!");
      } else {
        setSendStatus("Gagal: " + data.message);
      }
    } catch (err) {
      setSendStatus("Gagal mengirim pesan");
    }
  };

  const handleLogout = async () => {
    setLoadingLogout(true);
    try {
      await fetch("http://localhost:5000/logout", { method: "POST" });
      setConnected(false);
      setSendStatus("");
      setTo("");
      setText("");
    } catch (err) {
      //
    }
    setLoadingLogout(false);
  };

  return (
    <div className="min-h-screen flex bg-gray-100">
      {/* Sidebar */}
      <aside className="w-64 bg-green-700 text-white flex flex-col py-6 px-4">
        <div className="text-2xl font-bold mb-8 text-center">WA Gateway</div>
        <nav className="flex-1 space-y-4">
          <a href="#" className="block py-2 px-3 rounded hover:bg-green-800">Dashboard</a>
          <a href="#" className="block py-2 px-3 rounded hover:bg-green-800">Kirim Pesan</a>
        </nav>
        <div className="mt-8">
          <button
            onClick={handleLogout}
            disabled={loadingLogout}
            className="w-full bg-red-500 hover:bg-red-600 text-white py-2 rounded disabled:opacity-50"
          >
            {loadingLogout ? "Menghapus Sesi..." : "Logout / Hapus Sesi"}
          </button>
        </div>
      </aside>
      {/* Main Content */}
      <div className="flex-1 flex flex-col">
        {/* Navbar */}
        <header className="bg-white shadow px-8 py-4 flex items-center justify-between">
          <div className="text-xl font-semibold">WhatsApp Gateway</div>
          <div className="text-sm text-gray-600">{connected ? "Status: Terhubung" : "Status: Belum Terhubung"}</div>
        </header>
        {/* Content */}
        <main className="flex-1 p-8 flex flex-col items-center justify-center">
          <div className="w-full max-w-xl bg-white rounded shadow p-8">
            <h1 className="text-2xl font-bold mb-4 text-center">{message}</h1>
            {/* QR Code Section */}
            {!connected && qr && (
              <div className="mb-8 flex flex-col items-center">
                <p className="mb-2 font-semibold">Scan QR Code WhatsApp:</p>
                <QRCodeCanvas value={qr} size={256} />
              </div>
            )}
            {/* Form Kirim Pesan */}
            {connected && (
              <form onSubmit={handleSend} className="space-y-4">
                <div>
                  <input
                    type="text"
                    placeholder="Nomor WhatsApp (628xxxxxx)"
                    value={to}
                    onChange={(e) => setTo(e.target.value)}
                    className="border p-2 rounded w-full"
                    required
                  />
                </div>
                <div>
                  <textarea
                    placeholder="Isi pesan"
                    value={text}
                    onChange={(e) => setText(e.target.value)}
                    className="border p-2 rounded w-full"
                    required
                  />
                </div>
                <button
                  type="submit"
                  className="bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600"
                >
                  Kirim Pesan
                </button>
                {sendStatus && <div className="mt-2 text-sm">{sendStatus}</div>}
              </form>
            )}
          </div>
        </main>
      </div>
    </div>
  );
}

export default App;
